                        exist.StaffId = InputModel.StaffId;
                        exist.EmailConfirmed = true;
                        exist.LockoutEnabled = false;
                        exist.PhoneNumberConfirmed = true;
                        dbContext.Entry(exist).State = EntityState.Modified;
                        dbContext.SaveChanges();
                    }
                }
